    if (!options.target) {
      element.classList.add('ol-unselectable', 'ol-control');
      ol.ext.element.create('BUTTON', {
        type: 'button',
        title: options.title || 'Print',
        click: function () { this.print(); }.bind(this),
        parent: element
      });
    }
